var json = require("../json.js");


describe("Test functions in json.js.", function() {
    var data = [{"title": "Hello 1", "body": "yo"}, {"title": "Hello 2", "body": "hi"}];

    // json.require()
    it("checks if json.require() parses the specified file correctly.", function() {
        expect(json.require("spec/test.json")).toEqual(data);
    });

    // json.checkIfDataIsRequired()
    it("checks if json.checkIfDataIsRequired()) requires the file if given a string.", function() {
        expect(json.checkIfDataIsRequired("spec/test.json")).toEqual(data);
    });

});
